import React, { ReactElement } from "react";

export type Widget = {
    id: string;
    name: string;
    version: string;
    settings: unknown;
};
export type WidgetRender<WidgetProps> = React.FC<{
    settings: WidgetProps;
    widgetRenderer: (widget: Widget) => ReactElement<any, any> | null;
}>;
export type SettingsRender<WidgetProps> = React.FC<{
    settings: WidgetProps;
    onSettingsUpdate: (settings: WidgetProps) => void;
    widgetSelectionRenderer: (
        numberOfSlots: number,
        slots: Widget[] | undefined,
        onSlotsUpdate: (slots: Widget[]) => void
    ) => ReactElement<any, any> | null;
}>;

export interface WidgetInterface<WidgetProps> {
    id: string;
    version: string;
    name: string;
    render: WidgetRender<WidgetProps>;
    settingsRender: SettingsRender<WidgetProps>;
}
export default WidgetInterface;
